mapper.select:
select
  roomId, roomEnvironment, roomType, isReserved, price
from guestroom


        and roomId = #{roomid}


        and roomEnvironment = #{roomenvironment}


        and roomType = #{roomtype}


        and isReserved = #{isreserved}


        and price = #{price}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from guestroom


        and roomId = #{roomid}


        and roomEnvironment = #{roomenvironment}


        and roomType = #{roomtype}


        and isReserved = #{isreserved}


        and price = #{price}
