mapper.select:
select
  orderId, roomId, visitorId, administratorId, result
from roomorder


        and orderId = #{orderid}


        and roomId = #{roomid}


        and visitorId = #{visitorid}


        and administratorId = #{administratorid}


        and result = #{result}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from roomorder


        and orderId = #{orderid}


        and roomId = #{roomid}


        and visitorId = #{visitorid}


        and administratorId = #{administratorid}


        and result = #{result}
